import { StatusBar } from "expo-status-bar";
import React, { useState, useEffect } from "react";
import {
  ActivityIndicator,
  StyleSheet,
  Text,
  View,
  FlatList,
} from "react-native";
import moment from "moment";
import { Card, Button, Icon } from "react-native-elements";

export default function App() {
  const [loading, setLoading] = useState(true);
  const [articles, setArticles] = useState([]);

  const getNews = async () => {
    const response = await fetch(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=6eec2f7fe6cd4c40a3fef8f33f5778fe"
    );
    const jsonData = await response.json();
    // console.log("jsonData", jsonData);
    setArticles(jsonData.articles);
    setLoading(false);
  };

  useEffect(() => {
    getNews();
  }, []);

  if (loading) {
    return (
      <View style={styles.container}>
        <ActivityIndicator size="large" loading={loading} />
      </View>
    );
  }
  return (
    <View style={styles.container}>
      {articles.map((article) => {
        return (
          <Card title={article.title} image={{ uri: article.urlToImage }}>
            <View style={styles.row}>
              <Text style={styles.label}>Source</Text>
              <Text style={styles.info}>{article.source.name}</Text>
            </View>
            <Text style={{ marginBottom: 10 }}>{article.content}</Text>
            <View style={styles.row}>
              <Text style={styles.label}>Published</Text>
              <Text style={styles.info}>
                {moment(article.publishedAt).format("LLL")}
              </Text>
            </View>
            <Button
              icon={<Icon />}
              title="Read more"
              backgroundColor="#03A9F4"
            />
          </Card>
        );
      })}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  containerFlex: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  container: {
    flex: 1,
    marginTop: 40,
    alignItems: "center",
    backgroundColor: "#fff",
    justifyContent: "center",
  },
  header: {
    height: 30,
    width: "100%",
    backgroundColor: "pink",
  },
  row: {
    flexDirection: "row",
  },
  label: {
    fontSize: 16,
    color: "black",
    marginRight: 10,
    fontWeight: "bold",
  },
  info: {
    fontSize: 16,
    color: "grey",
  },
});
